INSERT INTO `cbt_soal` VALUES (264, 'PROBB', 'TO1TB', '', 13, '', 'Perhatikan data tabel data ukuran di bawah ini. Apabila akan membuat busana untuk peragawati tersebut, bagaimanakah cara menyesuaikan polanya?<br /><img src="/unbk/pictures//soal_no._13_.PNG" />', '', '', '', 'Lingkar pinggang pada pola ditambah 1 cm, lingkar badan ditambah &frac12; cm dan panjang punggung ditambah 11 cm', 'Lingkar pinggang pada pola ditambah 1 cm, lingkar badan ditambah &frac12; cm dan panjang punggung ditambah 11 cm', 'Lingkar pinggang pada pola ditambah &frac14; cm, lingkar badan ditambah &frac12; cm dan panjang punggung ditambah 1 cm', 'Lingkar pinggang pada pola dikurangi 1 cm, lingkar badan dikurangi &frac12; cm dan panjang punggung dikurangi 1 cm', 'Lingkar pinggang pada pola ditambah &frac12; cm, lingkar badan ditambah &frac12; cm dan pinggang punggung ditambah 1 cm', '', '', '', '', '', '5', 0, 0);
