INSERT INTO `cbt_soal` VALUES (261, 'PROBB', 'TO1TB', '', 10, '', 'Feed back adalah salah satu unsur komunikasi yang dihasilkan berupa tanggapan atau respon dari komunikan. Apabila respon yang diterima tidak dapat dimengerti oleh komunikator disebut...', '', '', '', 'Zero feedback', 'Feed back', 'Netral feed back', 'Negative feed back', 'Neutral feed back', '', '', '', '', '', '1', 0, 0);
INSERT INTO `cbt_soal` VALUES (262, 'PROBB', 'TO1TB', '', 11, '', 'Untuk mendapatkan gambar busana yang baik haruslah diketahui teknik penyelesaiannya. Teknik penyelesaian dengan menggunakan pensil (2B, 3B, 4B) yang benar adalah sebagai berikut, kecuali....', '', '', '', 'Jika seluruh gambar ingin diberi warna dengan pensil, goreskanlah pensil menurut arah benang', 'Siluet dari desain haruslah tebal, begitu juga bagian-bagian busana seperti kerah, saku, garis hias dan lain-lain.', 'Apabila desain dibuat dengan memakai kombinasi warna, maka warna yang lain dapat menggunakan spidol', 'Untuk membuat kesan halus dan garis kecil, pensil haruslah runcing atau pemakaiannya tidak ditekan', 'Agar gambar kelihatan hidup, maka diberi tekanan pada bagian-bagian tertentu, bagian yang tertimpa cahaya dibuat terang dan bagian lainnya dibuat gelap.', '', '', '', '', '', '5', 0, 0);
INSERT INTO `cbt_soal` VALUES (260, 'PROBB', 'TO1TB', '', 9, '', 'Nona NN adalah pelanggan Butik, minta dibuatkan baju pesta. Bentuk tubuhnya pendek gemuk. Desain seperti apakah yang dipilih agar penampilan Nona NN terlihat lebih ideal, tinggi dan langsing?', '', '', '', 'Siluet A, menggunakan garis princes, tekstur bahan berkilau, garis leher U, lengan kop, motif besar', 'Garis leher V, tekstur lembut, warna dingin, motif sedang, siluet I, menggunakan garis prinsces.', 'Kombinasi warna kontras, lengan tulip, tekstur bahan kaku, garis leher heart, garis hias yoke, motif garis horizontal', 'Siluet bustle, lengan reglan, garis hias yoke, rok kerut, motif kecil-kecil, kerah sanghay, warna panas', 'Lengan licin, garis leher sabrina, garis hias empire, warna dingin, motif sedang, tekstur bahan tembus terang', '', '', '', '', '', '2', 0, 0);
